import jsondef convert_to_jsonl(correct_file, incorrect_file, output_file):    """    Convert correct and incorrect data files into JSONL format for fine-tuning.    Args:        correct_file (str): Path to file containing correct data        incorrect_file (str): Path to file containing incorrect data        output_file (str): Path to output JSONL file    """    # Initialize the list to store all entries    jsonl_data = []    # Process correct data    with open(correct_file, 'r') as f:        correct_data = f.read().strip().split('\n')    # Process incorrect data    with open(incorrect_file, 'r') as f:        incorrect_data = f.read().strip().split('\n')    # Create entries for correct data    for input_data in correct_data:        entry = {            "messages": [                {                    "role": "system",                    "content": "Classify input as either correct or incorrect."                },                {                    "role": "user",                    "content": input_data                },                {                    "role": "assistant",                    "content": "correct"                }            ]        }        jsonl_data.append(entry)    # Create entries for incorrect data    for input_data in incorrect_data:        entry = {            "messages": [                {                    "role": "system",                    "content": "Classify input as either correct or incorrect."                },                {                    "role": "user",                    "content": input_data                },                {                    "role": "assistant",                    "content": "incorrect"                }            ]        }        jsonl_data.append(entry)    # Write to JSONL file    with open(output_file, 'w') as f:        for entry in jsonl_data:            f.write(json.dumps(entry) + '\n')